    getDateRange = function (startDate, endDate) {
        var start = new Date(startDate);
        // console.log(start);

        var end = new Date(endDate);
        // console.log(end);

        var tripDateRange = [];
        mil = 86400000 //24h
        for (var i = start.getTime(); i < end.getTime(); i = i + mil) {
            tripDateRange.push(new Date(i))
        }
        return tripDateRange;
    };
